mapper.select:
select
*
from auth_user u
where u.instid = #{instId}
and not exists(
select 1 from auth_account ac
where ac.appid = #{appId}
and ac.instid = #{instId}
and ac.userid = u.id
and ac.createtype='automatic'
)

	and (${filters})


	and u.departmentid in( ${orgIdsList})

mapper.delete:
delete from auth_account ac
where ac.instid = #{instId}
and ac.createtype = 'automatic'
and ac.appid = #{appId}
and
ac.strategyid = #{id}
and not exists(
select 1
from auth_user u
where instid = #{instId}
and u.id=ac.userid

	and (${filters})


	and u.departmentid in ( ${orgIdsList})

)
